export function DeliveriesIconCard({
  icon,
}: {
  icon: DeliveriesIcon;
}): JSX.Element {
  switch (icon) {
    case DeliveriesIcon.WhatsApp:
      return <WhatsAppIcon />;
    case DeliveriesIcon.Delivery:
      return <DeliveryIcon />;
    case DeliveriesIcon.Dessert:
      return <IceCreamIcon />;
    case DeliveriesIcon.IFood:
      return <IconIFood />;
    default:
      return <></>;
  }
}

export function InformationHeaderIcons({ icon }: { icon: HeaderIcon }) {
  switch (icon) {
    case HeaderIcon.Burguer:
      return <BurguerIcon />;
    case HeaderIcon.Fone:
      return <FoneIcon />;
    case HeaderIcon.Delivery:
      return <DeliveryIcon />;
    default:
      return <></>;
  }
}
